INSERT INTO `nation2player`(nation_id,player_id) SELECT transfermarket_nation.nation_ref_id,transfermarket_player.player_ref_id FROM `transfermarket_nation` JOIN `transfermarket_player` ON transfermarket_player.nation_id = transfermarket_nation.id WHERE CONCAT(transfermarket_nation.nation_ref_id,transfermarket_player.player_ref_id) NOT IN (SELECT CONCAT(nation_id, player_id) FROM `nation2player`) AND transfermarket_nation.nation_ref_id != 0 AND transfermarket_player.player_ref_id != 0